import React from "react";
import "../styles/home.css"; // Import styles
import MovieCard from "./MovieCard"; // Create a MovieCard component for each movie/show

const HomePage = () => {
  // Sample movie data
  const trendingMovies = [
    { id: 1, title: "Movie 1", image: "https://via.placeholder.com/150" },
    { id: 2, title: "Movie 2", image: "https://via.placeholder.com/150" },
  ];

  const popularMovies = [
    { id: 3, title: "Movie 3", image: "https://via.placeholder.com/150" },
    { id: 4, title: "Movie 4", image: "https://via.placeholder.com/150" },
  ];

  return (
    <div className="homepage">
      <nav className="navbar">
        <h2>OTT Platform</h2>
        <input type="text" placeholder="Search..." />
      </nav>

      <div className="banner">
        <h1>Featured Show</h1>
        <p>Watch the latest and greatest now!</p>
      </div>

      <section className="movies-section">
        <h2>Trending Now</h2>
        <div className="movies-container">
          {trendingMovies.map((movie) => (
            <MovieCard key={movie.id} movie={movie} />
          ))}
        </div>
      </section>

      <section className="movies-section">
        <h2>Popular Movies</h2>
        <div className="movies-container">
          {popularMovies.map((movie) => (
            <MovieCard key={movie.id} movie={movie} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
